declare module 'reactjs-emoji-keyboard' {
  type CategoryType = {
    slug: string;
    name: string;
    icon: string;
    subCategories: string[];
  };

  type EmojiType = {
    slug: string;
    character: string;
    unicodeName: string;
    codePoint: string;
    group: string;
    subGroup: string;
  };

  type Props = {
    width?: number;
    height?: number;
    theme?: 'light' | 'dark';
    searchDisabled?: boolean;
    searchLabel?: string;
    onEmojiSelect?: (item: EmojiType) => void;
  };
}
